/* main.cpp */

int main(int argc, const char * argv[]) {

    // Initialize variables as 2D vectors with 100 rows and 1 column
    vector<Eigen::VectorXd> x;
    vector<Eigen::VectorXd> y;

    // Fill x with values from 0 to 5
    for (int i = 0; i < 50; ++i) {
        Eigen::VectorXd g = Eigen::VectorXd::Random(3);
        x.push_back(g);
    }

    Eigen::MatrixXd w_sol(3, 2);
    w_sol << 1, 2,
             3, 4,
             5, 6;

    Eigen::VectorXd b_sol(2);
    b_sol << 4, 6;

    // Fill y with a linear functions
    for (int i = 0; i < 50; i++) {
        y.push_back(w_sol.transpose() * x[i] + b_sol);
    }

    NeuralNet nn(stof(argv[2])); // learning rate
    //nn.addLayer(1, nn.none);
    nn.eigenTrain(x, y, stoi(argv[1])); // epochs

    return 0;
}
